    fun getAllDepartments(): List<Department> {
        val twoYear = getTwoYearDepartments()
        val fourYear = getFourYearDepartments()
        val openEducation = getOpenEducationDepartmentsList()
        return (twoYear + fourYear + openEducation).sortedBy { it.name }
    }

    fun getDepartmentsByType(departmentType: DepartmentType): List<Department> {
        return when (departmentType) {
            DepartmentType.TWO_YEAR -> getTwoYearDepartments() + getOpenEducationDepartmentsList().filter { it.type == DepartmentType.TWO_YEAR }
            DepartmentType.FOUR_YEAR -> getFourYearDepartments() + getOpenEducationDepartmentsList().filter { it.type == DepartmentType.FOUR_YEAR }
            DepartmentType.OPEN_EDUCATION -> getOpenEducationDepartmentsList()
        }.sortedBy { it.name }
    }
